"use strict";(self.webpackChunkciri_docs=self.webpackChunkciri_docs||[]).push([[849],{6164(e){e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","href":"/docs/","label":"Introduction","docId":"index","unlisted":false},{"type":"link","href":"/docs/getting-started","label":"Getting Started","docId":"getting-started","unlisted":false},{"type":"category","label":"CLI Reference","collapsed":false,"items":[{"type":"link","href":"/docs/cli-reference","label":"CLI Reference","docId":"cli-reference","unlisted":false},{"type":"link","href":"/docs/examples","label":"Examples & Tutorials","docId":"examples","unlisted":false}],"collapsible":true},{"type":"category","label":"Features","collapsed":false,"items":[{"type":"link","href":"/docs/features/memory","label":"Persistent Workspace Memory","docId":"features/memory","unlisted":false},{"type":"link","href":"/docs/features/hitl","label":"Human-in-the-Loop (HITL)","docId":"features/hitl","unlisted":false},{"type":"link","href":"/docs/features/autocomplete","label":"Autocomplete & Context Triggers","docId":"features/autocomplete","unlisted":false},{"type":"link","href":"/docs/features/web-research","label":"Deep Web Research","docId":"features/web-research","unlisted":false},{"type":"link","href":"/docs/features/script-executor","label":"Script Executor","docId":"features/script-executor","unlisted":false}],"collapsible":true},{"type":"category","label":"Skills","collapsed":false,"items":[{"type":"link","href":"/docs/built-in-skills/","label":"Built-in Skills","docId":"built-in-skills/index","unlisted":false},{"type":"link","href":"/docs/skills-guide","label":"Skills Guide","docId":"skills-guide","unlisted":false}],"collapsible":true},{"type":"category","label":"Toolkits & Subagents","items":[{"type":"link","href":"/docs/toolkits-guide","label":"Toolkits Guide (MCP)","docId":"toolkits-guide","unlisted":false},{"type":"link","href":"/docs/subagents-guide","label":"SubAgents & Delegation","docId":"subagents-guide","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Architecture","items":[{"type":"link","href":"/docs/architecture/overview","label":"Architecture Overview","docId":"architecture/overview","unlisted":false},{"type":"link","href":"/docs/architecture/core-harness","label":"Core Harness Architecture","docId":"architecture/core-harness","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Internals","items":[{"type":"link","href":"/docs/internals/controller","label":"Copilot Controller","docId":"internals/controller","unlisted":false},{"type":"link","href":"/docs/internals/middlewares","label":"Middleware Stack","docId":"internals/middlewares","unlisted":false},{"type":"link","href":"/docs/internals/self-evolution","label":"Self-Evolution & Training","docId":"internals/self-evolution","unlisted":false},{"type":"link","href":"/docs/internals/graph-execution","label":"Graph Execution & Agent Boot","docId":"internals/graph-execution","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Project","items":[{"type":"link","href":"/docs/roadmap","label":"Roadmap","docId":"roadmap","unlisted":false},{"type":"link","href":"/docs/contributing","label":"Contributing to Ciri","docId":"contributing","unlisted":false},{"type":"link","href":"/docs/security","label":"Security & Privacy","docId":"security","unlisted":false},{"type":"link","href":"/docs/faq","label":"FAQ","docId":"faq","unlisted":false}],"collapsed":true,"collapsible":true}]},"docs":{"architecture/components":{"id":"architecture/components","title":"Architecture - Components","description":"CIRI\'s architecture is a modular stack where each component has a specific responsibility."},"architecture/components-deep":{"id":"architecture/components-deep","title":"Architecture - Components (Deep Dive)","description":"This page provides a high-resolution view of the CIRI internals and their implementation details."},"architecture/core-harness":{"id":"architecture/core-harness","title":"Core Harness Architecture","description":"CIRI uses a two-level harness to separate capabilities that belong to your entire development environment from those specific to a single project. This is one of the most important architectural decisions in CIRI \u2014 it\'s what makes skills, toolkits, and subagents reusable across all your workspaces.","sidebar":"tutorialSidebar"},"architecture/overview":{"id":"architecture/overview","title":"Architecture Overview","description":"CIRI is a local-first, multi-agent orchestration system built on LangChain and LangGraph. Its core design principle is extensibility: every capability can be added, modified, or replaced at runtime without restarting.","sidebar":"tutorialSidebar"},"architecture/skills-toolkits":{"id":"architecture/skills-toolkits","title":"Skills, Toolkits and Subagents","description":"CIRI is extended with three primary extension types:"},"built-in-skills/index":{"id":"built-in-skills/index","title":"Built-in Skills","description":"Ciri ships with 17 built-in skills that are bootstrapped into the global core harness (~/.local/share/ciri/skills/) on first run. These skills give Ciri immediate, expert-level capabilities across document processing, design, coding, communications, and more \u2014 in any workspace, without needing to train per-project.","sidebar":"tutorialSidebar"},"cli-reference":{"id":"cli-reference","title":"CLI Reference","description":"Ciri\'s terminal interface is built on Prompt Toolkit for rich, interactive input handling. Everything you type at the You > prompt is either a natural language request, a slash command, or an @-autocomplete trigger.","sidebar":"tutorialSidebar"},"cli-reference-advanced":{"id":"cli-reference-advanced","title":"CLI - Advanced Usage and Integration","description":"This page documents deeper integration points and automation patterns."},"contributing":{"id":"contributing","title":"Contributing to Ciri","description":"Thank you for your interest in making Ciri better. This guide covers how to set up a development environment, the project structure, contribution workflows, and how to extend Ciri with new skills, toolkits, and subagents.","sidebar":"tutorialSidebar"},"development/build-packaging":{"id":"development/build-packaging","title":"Build & Packaging","description":"CIRI supports distribution as a wheel (recommended) and a single-file binary via PyInstaller (optional)."},"development/run-debug":{"id":"development/run-debug","title":"Run & Debug","description":"Run the CLI:"},"development/setup":{"id":"development/setup","title":"Development Setup","description":"This page covers a reproducible development environment."},"development/tests-lint":{"id":"development/tests-lint","title":"Tests & Linting","description":"Testing"},"examples":{"id":"examples","title":"Examples & Tutorials","description":"Hands-on walkthroughs showing Ciri in action across different domains and workflows.","sidebar":"tutorialSidebar"},"faq":{"id":"faq","title":"FAQ","description":"Answers to the most common questions about Ciri.","sidebar":"tutorialSidebar"},"features/autocomplete":{"id":"features/autocomplete","title":"Autocomplete & Context Triggers","description":"Ciri\'s terminal input supports two families of autocomplete:","sidebar":"tutorialSidebar"},"features/hitl":{"id":"features/hitl","title":"Human-in-the-Loop (HITL)","description":"Ciri\'s Human-in-the-Loop system is a mandatory safety gate built into the HumanInTheLoopMiddleware at the LangGraph level. Before Ciri executes any consequential action \u2014 writing a file, running a script, calling an external service \u2014 she pauses and presents a richly formatted approval prompt. You inspect, approve, edit, or reject every proposed action.","sidebar":"tutorialSidebar"},"features/memory":{"id":"features/memory","title":"Persistent Workspace Memory","description":"Ciri maintains a long-term understanding of your workspace through a two-level markdown-based memory system. This is not a database or vector store \u2014 it is a curated set of .md files that are injected into every single conversation turn, ensuring Ciri always has your context at hand.","sidebar":"tutorialSidebar"},"features/script-executor":{"id":"features/script-executor","title":"Script Executor","description":"The execute_script tool gives Ciri the ability to run arbitrary Python or JavaScript code in a fully isolated, sandboxed environment \u2014 with your explicit approval before anything runs. It handles dependency installation, execution, output collection, and cleanup automatically.","sidebar":"tutorialSidebar"},"features/web-research":{"id":"features/web-research","title":"Deep Web Research","description":"Ciri\'s web_researcher subagent is not a basic scraper. It operates your real browser via Chrome DevTools Protocol (CDP), using your actual sessions, cookies, and login state. This means Ciri can research inside authenticated apps \u2014 your company\'s internal wiki, AWS console, GitHub private repos, Salesforce, Google Analytics \u2014 anywhere your browser is already logged in.","sidebar":"tutorialSidebar"},"getting-started":{"id":"getting-started","title":"Getting Started","description":"This guide covers installation, first-run setup, and the essential commands to get Ciri working in your workspace in under five minutes.","sidebar":"tutorialSidebar"},"index":{"id":"index","title":"Introduction","description":"Ciri is an open-source, autonomous AI copilot that lives inside your workspace. She is not a coding assistant \u2014 she is a general-purpose intelligence that adapts to whatever domain you work in: software engineering, accounting, marketing, branding, sales, legal research, content production, data analysis, or any discipline you train her on.","sidebar":"tutorialSidebar"},"internals/controller":{"id":"internals/controller","title":"Copilot Controller","description":"The CopilotController is the primary interface for executing the CIRI agent graph. It handles conversation threads, state management, and multimodal input processing.","sidebar":"tutorialSidebar"},"internals/graph-execution":{"id":"internals/graph-execution","title":"Graph Execution & Agent Boot","description":"This page traces exactly how Ciri is constructed and executed at runtime \u2014 from the moment ciri is run in the terminal to the first token streaming back. Reference this when adding middlewares, subagents, or custom tools.","sidebar":"tutorialSidebar"},"internals/middlewares":{"id":"internals/middlewares","title":"Middleware Stack","description":"Ciri\'s intelligence is not monolithic \u2014 it is assembled from a layered middleware stack that transforms every agent turn. Each middleware wraps the LLM call to inject context, enforce safety, manage state, or add capabilities.","sidebar":"tutorialSidebar"},"internals/self-evolution":{"id":"internals/self-evolution","title":"Self-Evolution & Training","description":"Ciri is designed to become a better version of herself over time \u2014 specialized for your exact workspace rather than remaining a generic assistant. The /sync command triggers the Trainer Agent, which orchestrates a structured AUDIT \u2192 ANALYZE \u2192 PLAN \u2192 BUILD \u2192 VERIFY loop.","sidebar":"tutorialSidebar"},"roadmap":{"id":"roadmap","title":"Roadmap","description":"Ciri\'s development priorities \u2014 where we are, where we\'re going, and what the community can help with.","sidebar":"tutorialSidebar"},"security":{"id":"security","title":"Security & Privacy","description":"Ciri is designed to be local-first: your state lives on your machine, your keys stay in your environment, and you approve every consequential action before it runs. This guide explains the full security model.","sidebar":"tutorialSidebar"},"skills-guide":{"id":"skills-guide","title":"Skills Guide","description":"Skills are the fundamental building blocks of Ciri\'s domain knowledge. A skill is a self-contained package consisting of a SKILL.md playbook and optional Python helper scripts. Skills tell Ciri when to activate and how to approach tasks in a specific domain \u2014 whether that domain is PDF processing, financial reporting, marketing copy, or legal document review.","sidebar":"tutorialSidebar"},"subagents-guide":{"id":"subagents-guide","title":"SubAgents & Delegation","description":"SubAgents are specialized agent instances with their own system prompt, tool scope, and reasoning context. Where Ciri is a generalist, a subagent is an expert called in for a specific class of task. The SubAgentMiddleware manages routing, handoff, and result integration.","sidebar":"tutorialSidebar"},"toolkits-guide":{"id":"toolkits-guide","title":"Toolkits Guide (MCP)","description":"Toolkits are MCP (Model Context Protocol) servers that give Ciri live, bidirectional access to external services \u2014 databases, SaaS APIs, cloud platforms, internal tools, and anything else that can be wrapped in an MCP interface. Ciri\'s ToolkitInjectionMiddleware acts as an MCP client that discovers, connects to, and hot-reloads these servers without requiring a restart.","sidebar":"tutorialSidebar"},"use-cases/advanced-research":{"id":"use-cases/advanced-research","title":"Advanced Research Assistant (Detailed)","description":"This guide walks through a full research workflow using CIRI: web crawling, extraction, embedding, retrieval, and summarization. It ties implementation to real files and shows what to expect."},"use-cases/code-refactor":{"id":"use-cases/code-refactor","title":"Code Refactor Flow (Human-in-the-loop)","description":"This tutorial demonstrates a common developer workflow where CIRI proposes a code fix or refactor, you review it, and CIRI applies it and runs tests."},"use-cases/index":{"id":"use-cases/index","title":"Use Cases & Tutorials","description":"This section contains practical, real-world workflows for CIRI. Pick a tutorial to get hands-on with common tasks:"},"use-cases/release-automation":{"id":"use-cases/release-automation","title":"Release Automation (Packaging & Publishing)","description":"This tutorial shows how to build a wheel and prepare a release using hatchling and twine, and how to integrate this into CI."}}}}')}}]);